import{_ as a,c as t,o as s,at as i}from"./chunks/framework.Cnsi11we.js";const h=JSON.parse('{"title":"API Documentation","description":"Programmatic access to Arabic maqāmāt, ajnās, and tuning systems data","frontmatter":{"title":"API Documentation","description":"Programmatic access to Arabic maqāmāt, ajnās, and tuning systems data"},"headers":[],"relativePath":"api/index.md","filePath":"api/index.md","lastUpdated":1762641986000}'),n={name:"api/index.md"};function o(r,e,l,c,d,m){return s(),t("div",null,[...e[0]||(e[0]=[i('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /docs/api.md for this page in Markdown format</div><h1 id="api-documentation" tabindex="-1">API Documentation <a class="header-anchor" href="#api-documentation" aria-label="Permalink to &quot;API Documentation&quot;">​</a></h1><p>Digital Arabic Maqām Archive (DiArMaqAr) — programmatic access to historically documented maqāmāt, ajnās, and tuning systems spanning from al-Kindī (874 CE) to contemporary approaches.</p><h2 id="quick-start" tabindex="-1">Quick Start <a class="header-anchor" href="#quick-start" aria-label="Permalink to &quot;Quick Start&quot;">​</a></h2><p><strong>Try this first:</strong></p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">curl</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;https://diarmaqar.netlify.app/api/maqamat/maqam_rast?tuningSystem=IbnSina-(1037)&amp;startingNote=yegah&amp;pitchClassDataType=cents&quot;</span></span></code></pre></div><p><strong>Base URL:</strong> <code>https://diarmaqar.netlify.app/api</code></p><h2 id="documentation-resources" tabindex="-1">Documentation Resources <a class="header-anchor" href="#documentation-resources" aria-label="Permalink to &quot;Documentation Resources&quot;">​</a></h2><h3 id="for-humans" tabindex="-1">For Humans <a class="header-anchor" href="#for-humans" aria-label="Permalink to &quot;For Humans&quot;">​</a></h3><ul><li><strong><a href="./endpoints-reference">Endpoints Reference</a></strong> - Complete endpoint documentation with parameters and responses</li><li><strong><a href="./playground">Interactive Playground</a></strong> - Test API calls live in your browser</li><li><strong><a href="./representative-examples">Representative Examples</a></strong> - Recommended examples for consistent documentation</li></ul><h3 id="for-llms" tabindex="-1">For LLMs <a class="header-anchor" href="#for-llms" aria-label="Permalink to &quot;For LLMs&quot;">​</a></h3><ul><li><strong>OpenAPI Specification:</strong> <a href="/docs/docs/openapi.json">openapi.json</a> | <a href="/docs/docs/openapi.yaml">openapi.yaml</a></li><li><strong>LLM-optimized:</strong> <a href="/docs/docs/llms.txt">llms.txt</a> | <a href="/docs/docs/llms-full.txt">llms-full.txt</a></li></ul><hr><h2 id="api-endpoints" tabindex="-1">API Endpoints <a class="header-anchor" href="#api-endpoints" aria-label="Permalink to &quot;API Endpoints&quot;">​</a></h2><h3 id="maqamat" tabindex="-1">Maqāmāt <a class="header-anchor" href="#maqamat" aria-label="Permalink to &quot;Maqāmāt&quot;">​</a></h3><p>Documented modal frameworks with historical source attribution.</p><p><strong>Endpoints:</strong></p><ul><li><code>GET /maqamat</code> - List all maqāmāt → <a href="./endpoints-reference#list-all-maqamat">Details</a></li><li><code>GET /maqamat/{id}</code> - Get maqām details → <a href="./endpoints-reference#get-maqam-details">Details</a></li><li><code>GET /maqamat/{id}/availability</code> - Check availability → <a href="./endpoints-reference#check-maqam-availability">Details</a></li><li><code>GET /maqamat/{id}/transpositions</code> - List transpositions → <a href="./endpoints-reference#list-maqam-transpositions">Details</a></li><li><code>GET /maqamat/{id}/compare</code> - Compare across systems → <a href="./endpoints-reference#compare-maqam-across-tuning-systems">Details</a></li></ul><h3 id="ajnas" tabindex="-1">Ajnās <a class="header-anchor" href="#ajnas" aria-label="Permalink to &quot;Ajnās&quot;">​</a></h3><p>Documented melodic fragments with historical source attribution.</p><p><strong>Endpoints:</strong></p><ul><li><code>GET /ajnas</code> - List all ajnās → <a href="./endpoints-reference#list-all-ajnas">Details</a></li><li><code>GET /ajnas/{id}</code> - Get jins details → <a href="./endpoints-reference#get-jins-details">Details</a></li><li><code>GET /ajnas/{id}/availability</code> - Check availability → <a href="./endpoints-reference#check-jins-availability">Details</a></li><li><code>GET /ajnas/{id}/transpositions</code> - List transpositions → <a href="./endpoints-reference#list-jins-transpositions">Details</a></li><li><code>GET /ajnas/{id}/compare</code> - Compare across systems → <a href="./endpoints-reference#compare-jins-across-tuning-systems">Details</a></li></ul><h3 id="tuning-systems" tabindex="-1">Tuning Systems <a class="header-anchor" href="#tuning-systems" aria-label="Permalink to &quot;Tuning Systems&quot;">​</a></h3><p>Historical tuning systems from al-Kindī (874 CE) to contemporary approaches.</p><p><strong>Endpoints:</strong></p><ul><li><code>GET /tuning-systems</code> - List all tuning systems → <a href="./endpoints-reference#list-all-tuning-systems">Details</a></li><li><code>GET /tuning-systems/{id}/{startingNote}/pitch-classes</code> - Get pitch classes → <a href="./endpoints-reference#get-tuning-system-pitch-classes">Details</a></li><li><code>GET /tuning-systems/{id}/{startingNote}/maqamat</code> - List maqāmāt in system → <a href="./endpoints-reference#list-maqamat-in-tuning-system">Details</a></li></ul><h3 id="pitch-classes-by-note-names" tabindex="-1">Pitch Classes (by Note Names) <a class="header-anchor" href="#pitch-classes-by-note-names" aria-label="Permalink to &quot;Pitch Classes (by Note Names)&quot;">​</a></h3><p>Query pitch class data by note name across tuning systems.</p><p><strong>Endpoints:</strong></p><ul><li><code>GET /pitch-classes/note-names</code> - List all note names → <a href="./endpoints-reference#list-note-names">Details</a></li><li><code>GET /pitch-classes/note-names/{noteName}</code> - Get pitch class by note name → <a href="./endpoints-reference#get-pitch-class-by-note-name">Details</a></li><li><code>GET /pitch-classes/note-names/{noteName}/availability</code> - Check availability → <a href="./endpoints-reference#check-note-name-availability">Details</a></li><li><code>GET /pitch-classes/note-names/{noteName}/compare</code> - Compare across systems → <a href="./endpoints-reference#compare-pitch-class-across-systems">Details</a></li></ul><h3 id="intervals" tabindex="-1">Intervals <a class="header-anchor" href="#intervals" aria-label="Permalink to &quot;Intervals&quot;">​</a></h3><p>Calculate intervals between note names.</p><p><strong>Endpoints:</strong></p><ul><li><code>GET /intervals</code> - Calculate intervals by note names → <a href="./endpoints-reference#calculate-intervals-by-note-names">Details</a></li><li><code>GET /intervals/compare</code> - Compare intervals across systems → <a href="./endpoints-reference#compare-intervals-across-tuning-systems">Details</a></li></ul><h3 id="sources" tabindex="-1">Sources <a class="header-anchor" href="#sources" aria-label="Permalink to &quot;Sources&quot;">​</a></h3><p>Bibliographic references for all documented content.</p><p><strong>Endpoints:</strong></p><ul><li><code>GET /sources</code> - List all sources → <a href="./endpoints-reference#list-all-sources">Details</a></li><li><code>GET /sources/{id}</code> - Get source details → <a href="./endpoints-reference#get-source-details">Details</a></li><li><code>GET /sources/{id}/tuning-systems</code> - List tuning systems by source → <a href="./endpoints-reference#list-tuning-systems-by-source">Details</a></li><li><code>GET /sources/{id}/maqamat</code> - List maqāmāt by source → <a href="./endpoints-reference#list-maqamat-by-source">Details</a></li><li><code>GET /sources/{id}/ajnas</code> - List ajnās by source → <a href="./endpoints-reference#list-ajnas-by-source">Details</a></li></ul><hr><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h2><p>All endpoints return JSON. No authentication required.</p><p><strong>Common parameters:</strong></p><ul><li><code>includeArabic</code> (default: &quot;true&quot;) - Include Arabic script alongside transliteration</li><li><code>includeSources</code> (default varies) - Include bibliographic source references</li></ul><p>For complete parameter documentation, see the <a href="./endpoints-reference">Endpoints Reference</a>.</p><p>For recommended example values, see <a href="./representative-examples">Representative Examples</a>.</p>',45)])])}const u=a(n,[["render",o]]);export{h as __pageData,u as default};
