import{_ as t,c as e,o as a,at as o}from"./chunks/framework.Cnsi11we.js";const d=JSON.parse('{"title":"MIDI Integration","description":"MIDI input/output capabilities including MPE support for microtonal playback","frontmatter":{"title":"MIDI Integration","description":"MIDI input/output capabilities including MPE support for microtonal playback"},"headers":[],"relativePath":"guide/midi-integration.md","filePath":"guide/midi-integration.md","lastUpdated":null}'),n={name:"guide/midi-integration.md"};function l(r,i,s,c,u,h){return a(),e("div",null,[...i[0]||(i[0]=[o('<h1 id="midi-integration" tabindex="-1">MIDI Integration <a class="header-anchor" href="#midi-integration" aria-label="Permalink to &quot;MIDI Integration&quot;">​</a></h1><p>DiArMaqAr implements sophisticated MIDI integration allowing users to interact with any tuning system, or the subsequences of ajnās or maqāmāt, through both computer keyboard and MIDI controller input, with precise microtonal output capabilities.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The platform supports comprehensive MIDI functionality:</p><ul><li><strong>Input</strong>: Computer keyboard and MIDI controllers</li><li><strong>Output</strong>: Monophonic and MPE (MIDI Polyphonic Expression)</li><li><strong>Precision</strong>: 14-bit pitch bend for accurate microtonal intervals</li><li><strong>Real-time</strong>: Low-latency response for live exploration</li></ul><h2 id="midi-input" tabindex="-1">MIDI Input <a class="header-anchor" href="#midi-input" aria-label="Permalink to &quot;MIDI Input&quot;">​</a></h2><h3 id="computer-keyboard" tabindex="-1">Computer Keyboard <a class="header-anchor" href="#computer-keyboard" aria-label="Permalink to &quot;Computer Keyboard&quot;">​</a></h3><ul><li>QWERTY keyboard mapping to pitch classes</li><li>Visual feedback on interface</li><li>Immediate response</li><li>No additional hardware required</li></ul><h3 id="midi-controllers" tabindex="-1">MIDI Controllers <a class="header-anchor" href="#midi-controllers" aria-label="Permalink to &quot;MIDI Controllers&quot;">​</a></h3><ul><li>External MIDI device support</li><li>Real-time input processing</li><li>Low-latency response</li><li>Works with any MIDI-compatible controller</li></ul><p><strong>Setup:</strong></p><ol><li>Connect MIDI controller to computer</li><li>Browser will detect available MIDI devices</li><li>Select device from interface</li><li>Start playing</li></ol><h2 id="midi-output" tabindex="-1">MIDI Output <a class="header-anchor" href="#midi-output" aria-label="Permalink to &quot;MIDI Output&quot;">​</a></h2><h3 id="monophonic-output" tabindex="-1">Monophonic Output <a class="header-anchor" href="#monophonic-output" aria-label="Permalink to &quot;Monophonic Output&quot;">​</a></h3><ul><li>Standard MIDI protocol</li><li>Single channel output</li><li>Wide accessibility</li><li>Compatible with most software and hardware</li></ul><h3 id="mpe-midi-polyphonic-expression" tabindex="-1">MPE (MIDI Polyphonic Expression) <a class="header-anchor" href="#mpe-midi-polyphonic-expression" aria-label="Permalink to &quot;MPE (MIDI Polyphonic Expression)&quot;">​</a></h3><p><strong>What is MPE?</strong> MIDI Polyphonic Expression enables accurate tuning of polyphonic software and hardware synthesizers without tuning artifacts. Traditional MIDI uses pitch bend on a single channel for all voices, causing conflicts when playing microtonal harmonies.</p><p><strong>How MPE Works:</strong></p><ul><li>Each voice gets its own MIDI channel</li><li>Individual pitch bend per channel</li><li>No conflicts between simultaneous pitches</li><li>Precise microtonal intervals preserved</li></ul><p><strong>MPE Precision:</strong></p><ul><li><strong>14-bit pitch bend</strong>: High resolution control messages</li><li><strong>Accurate conversion</strong>: Fractional ratios and cents converted to precise MIDI values</li><li><strong>No artifacts</strong>: Pitch relationships remain accurate in digital audio workstations</li><li><strong>Polyphonic harmony</strong>: Multiple microtonal pitches simultaneously</li></ul><h2 id="pitch-bend-calculation" tabindex="-1">Pitch Bend Calculation <a class="header-anchor" href="#pitch-bend-calculation" aria-label="Permalink to &quot;Pitch Bend Calculation&quot;">​</a></h2><p>The system uses 14-bit pitch bend calculations to achieve precise microtonal intervals:</p><p><strong>Process:</strong></p><ol><li>Converts fractional ratios/cents to target frequency</li><li>Calculates deviation from 12-EDO equivalent</li><li>Generates 14-bit pitch bend value</li><li>Sends on appropriate MIDI channel (MPE) or channel 1 (monophonic)</li></ol><p><strong>Precision:</strong></p><ul><li>Maintains mathematical accuracy</li><li>Preserves pitch relationships essential to traditional practice</li><li>Enables authentic microtonal playback in external systems</li></ul><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to &quot;Use Cases&quot;">​</a></h2><h3 id="live-performance" tabindex="-1">Live Performance <a class="header-anchor" href="#live-performance" aria-label="Permalink to &quot;Live Performance&quot;">​</a></h3><ul><li>Perform maqāmāt using MIDI controllers</li><li>Real-time exploration of tuning systems</li><li>Integration with external synthesizers</li></ul><h3 id="composition" tabindex="-1">Composition <a class="header-anchor" href="#composition" aria-label="Permalink to &quot;Composition&quot;">​</a></h3><ul><li>Export MIDI data to DAWs</li><li>Use MPE-capable instruments</li><li>Create compositions with authentic tunings</li></ul><h3 id="research" tabindex="-1">Research <a class="header-anchor" href="#research" aria-label="Permalink to &quot;Research&quot;">​</a></h3><ul><li>Audition theoretical data through external systems</li><li>Compare different synthesizers/samplers</li><li>Test tuning system compatibility</li></ul><h3 id="education" tabindex="-1">Education <a class="header-anchor" href="#education" aria-label="Permalink to &quot;Education&quot;">​</a></h3><ul><li>Interactive learning with MIDI keyboards</li><li>Hands-on exploration of maqāmic relationships</li><li>Integration with music education tools</li></ul><h2 id="technical-details" tabindex="-1">Technical Details <a class="header-anchor" href="#technical-details" aria-label="Permalink to &quot;Technical Details&quot;">​</a></h2><h3 id="voice-distribution-mpe" tabindex="-1">Voice Distribution (MPE) <a class="header-anchor" href="#voice-distribution-mpe" aria-label="Permalink to &quot;Voice Distribution (MPE)&quot;">​</a></h3><p>The system manages voice distribution across multiple MIDI channels:</p><ul><li>Automatic channel assignment</li><li>Voice allocation and release</li><li>Conflict prevention</li><li>Efficient channel management</li></ul><h3 id="latency" tabindex="-1">Latency <a class="header-anchor" href="#latency" aria-label="Permalink to &quot;Latency&quot;">​</a></h3><ul><li><strong>Low-latency processing</strong>: Real-time input handling</li><li><strong>Optimized routing</strong>: Direct MIDI I/O</li><li><strong>Browser compatibility</strong>: Works with Web MIDI API</li></ul><h3 id="compatibility" tabindex="-1">Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;Compatibility&quot;">​</a></h3><ul><li><strong>Hardware</strong>: Any MIDI-compatible controller</li><li><strong>Software</strong>: DAWs supporting MIDI and MPE</li><li><strong>Browsers</strong>: Chrome, Edge, Opera (Web MIDI API support)</li></ul><h2 id="integration-with-daws" tabindex="-1">Integration with DAWs <a class="header-anchor" href="#integration-with-daws" aria-label="Permalink to &quot;Integration with DAWs&quot;">​</a></h2><p>DiArMaqAr MIDI output can be used with:</p><ul><li><strong>Ableton Live</strong>: MPE support for microtonal playback</li><li><strong>Logic Pro</strong>: MIDI and MPE compatibility</li><li><strong>Bitwig Studio</strong>: Native MPE support</li><li><strong>Reaper</strong>: MIDI routing capabilities</li><li><strong>Any DAW</strong>: Basic MIDI output (monophonic mode)</li></ul><h2 id="limitations" tabindex="-1">Limitations <a class="header-anchor" href="#limitations" aria-label="Permalink to &quot;Limitations&quot;">​</a></h2><ul><li><strong>Browser support</strong>: Web MIDI API not available in all browsers</li><li><strong>MPE requirement</strong>: External systems must support MPE for polyphonic microtonal playback</li><li><strong>Latency</strong>: Browser audio processing may introduce slight latency</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li>Learn about <a href="/docs/guide/audio-synthesis/">Audio Synthesis</a> capabilities</li><li>Explore <a href="/docs/guide/data-export/">Data Export</a> including Scala formats</li><li>Understand <a href="/docs/guide/tuning-systems/">Tuning Systems</a> for MIDI applications</li></ul>',51)])])}const m=t(n,[["render",l]]);export{d as __pageData,m as default};
